import axios from 'axios'
import swal from 'sweetalert2'

export default {
  data(){
    return{
      date: null,
      dialog4: false,
      first:'',
      date:'',
      listTodo: [],
      dataWeather : {},
    }
  },
  methods:{
    editTodo(id){
      let statusTodo = {status : true}
      let tokenUser = localStorage.getItem('token')
      let self = this
      axios.put('https://todoserver.neil.guru/todos/update/'+id,statusTodo,{
        headers:{
          tokenUser
        },
      })
      .then((value) => {
        self.getData()
      })
      .catch((err) => {
        console.log(err);
      })
    },
    editedTodo(id){
      let statusTodo = {status : false}
      let tokenUser = localStorage.getItem('token')
      let self = this
      axios.put('https://todoserver.neil.guru/todos/update/'+id,statusTodo,{
        headers:{
          tokenUser
        },
      })
      .then((value) => {
        self.getData()
        })
      .catch((err) => {
        console.log(err);
      })
    },
    addTodo(){
      if(localStorage.getItem('token')){
        if(this.date === '' && this.first === ''){
          swal({
            type: 'error',
            title: 'Please Insert',
            text: 'Todo and Date',
          })
        }else{
          let sentTodo = {
            description : this.first,
            date: this.date
          }
          let tokenuser = localStorage.getItem('token')
          let self = this
          axios.post('https://todoserver.neil.guru/todos/create',sentTodo,{
            headers:{
              tokenuser
            }
          })
          .then((value) => {
            self.first = ''
            self.date = ''
            swal(
              'Good job!',
              'You clicked the button!',
              'success'
            )
            self.getData()
          })
          .catch((err) => {
            console.log(err);
          })
        }
      }else{
        swal({
          type: 'error',
          title: 'Please Login First',
        })
      }
    },
    getData(){
      let token = localStorage.getItem('token')
      let self = this
      axios.get('https://todoserver.neil.guru/todos/datatodo',{
        headers:{
          token
        }
      })
      .then((value) => {
        self.listTodo = value.data.data.todo
        console.log(value.data.data.todo);
      })
      .catch((err) => {
        console.log(err);
      })
    },
    deleteTodo (id) {
      let token = localStorage.getItem('token')
        axios.delete('https://todoserver.neil.guru/todos/deletetodo/'+id,{
          headers:{
            token
          }
        })
        .then((value) => {
          swal("Oooops", "You deleted a Todo!", "error");
          this.getData()
        })
        .catch((err) => {
          console.log(err);
        })
      },
    getWeather(){
      let self = this
      axios.get('https://todoserver.neil.guru/todos/getweather')
      .then((value) => {
        let data = value.data.result[0]
        self.dataWeather = {temperature: data.current.temperature, image : data.current.imageUrl, location : data.location.name}
      })
      .catch((err) => {
        console.log(err);
      })
    },
    getScored(){
      let self = this
        axios.get('https://raw.githubusercontent.com/openfootball/world-cup.json/master/2018/worldcup.json')
        .then((value) => {
          console.log(value);
        })
        .catch((err) => {
          console.log(err);
        })
      }
  },
  created(){
    this.getData()
    this.getWeather()
  }
}
